INSERT INTO cards (category, name_ru, name_en, desc_ru, desc_en, quote_ru, quote_en, facts_ru, facts_en, answers_en, drawing_id, pixelated_id, screenshot_id, bg_color_1, answers_ru, completed, bg_color_2, text_color) VALUES ('movie', 'Леон', 'Léon: The Professional', e'Профессиональный убийца Леон, не знающий пощады и жалости, знакомится со своей очаровательной соседкой Матильдой, семью которой расстреливают полицейские, замешанные в торговле наркотиками. Благодаря этому знакомству он впервые испытывает чувство любви, но…', '12-year-old Mathilda is reluctantly taken in by Léon, a professional assassin, after her family is murdered. An unusual relationship forms as she becomes his protégée and learns the assassin''s trade.', 'Это мой лучший друг: всегда в хорошем настроении и не задаёт лишних вопросов', 'This is my best friend: always in a good mood and does not ask unnecessary questions', '["Это дебютная полнометражная кар-\nтина Натали Портман. Когда ее взяли \nна роль, ей было 11 лет", "Все сцены в квартире Леона снима-\nлись в Париже, а сцены в коридоре \n— за шесть недель до этого в Нью-\nЙорке", "Фраза героя Жана Рено «Свиньи \nлучше, чем люди» — дань уважения \nХаяо Миядзаки и роли в «Порко \nРоссо» (1992)"]', '["This is Natalie Portman''s motion picture debut. She was 11 years old when she was cast", "All of the interiors of Léon''s apartment were shot in Paris, France", "All of the shots of the outside corridor were shot six weeks earlier in New York, USA", "Natalie Portman beat out 2000 other actresses for the role of Mathilda."]', '["Léon: The Professional", "Fight Club", "Forrest Gump", "Apocalypse Now", "The Twilight"]', 'BQACAgIAAxkBAAPRZRU8ONnrunyXT_0hvJ7JqEYf7uAAAmo0AAKtcLBI3yxcomxzrXUwBA', 'BQACAgIAAxkBAAPTZRU8Q47YxFeiuAeFRpO9VcTlMcwAAmw0AAKtcLBIVcZscYzGzPgwBA', 'BQACAgIAAxkBAAPVZRU8S7KaVmvgjewM9PpIpsvMzHIAAm00AAKtcLBIu4D5bIZ0wOQwBA', '#F4B1C9', '["Леон", "Бойцовский Клуб", "Форрест Гамп", "Апокалипсис Сегодня", "Сумерки"]', true, '#C4E4EA', '#00008b');
INSERT INTO cards (category, name_ru, name_en, desc_ru, desc_en, quote_ru, quote_en, facts_ru, facts_en, answers_en, drawing_id, pixelated_id, screenshot_id, bg_color_1, answers_ru, completed, bg_color_2, text_color) VALUES ('movie', 'Исчезнувшая', 'Gone Girl', e'День, который должен был стать праздником, обернулся кошмаром: пятая годовщина свадьбы ознаменовалась исчезновением Эмы. Следы, улики, логика - всё указывает на вину ее мужа Ника. Но как-то слишком безупречно выстраивается сценарий преступления. Только найденный дневник Эмы расставляет всё по своим местам, и злодейство приобретает совсем иной характер', 'With his wife''s disappearance having become the focus of an intense media circus, a man sees the spotlight turned on him when it''s suspected that he may not be innocent', e'Брак это тяжёлый труд, а ещё это компромисс и снова тяжёлый труд, оставь надежду всяк сюда входящий', 'Marriage is compromise and hard work, and then more hard work and compromise. And then work. Abandon all hope, ye who enter', '["Это первый полнометражный фильм, \nснятый полностью в разрешении 6k \n(6144 x 3160)", "Автор романа-первоисточника Гил-\nлиан Флинн написала несколько \nвариантов финала сценария фильма, \nчтобы сохранить интерес читателей \nи зрителей, а также чтобы избежать \nспойлеров"]', '["\"The Bar\" restaurant in the movie opened as a functional bar and restaurant after filming and operated for a few years. It has since permanently closed", "Originally, Nick was supposed to wear a Yankees cap in one scene. But Ben Affleck, a die-hard Red Sox fan, refused to wear it. Ultimately, he and David Fincher compromised, and he wears a Mets cap", "The whole play with the pens of different colors was Gillian Flynn''s visual solution to the translation to screen of Amy''s diary"]', '["Gone Girl", "American Beauty", "Basic Instinct", "The Girl with the Dragon Tattoo", "The Game"]', 'BQACAgIAAxkBAAPxZRVAwuDHpQKgS1O3NIl6zgfIX0cAApg0AAKtcLBIUY14fQABwrejMAQ', 'BQACAgIAAxkBAAPzZRVAzbaOZ8UTZLUBj3SIz5sPB3UAApk0AAKtcLBIF-PElc9WfCwwBA', 'BQACAgIAAxkBAAP1ZRVA2h4BfsRkn-CCWfaEOPRHTYIAApo0AAKtcLBIFQmir8Gry64wBA', '#677497', '["Исчезнувшая", "Красота по-американски", "Основной Инстинкт", "Девушка с Татуировкой Дракона", "Игра"]', true, '#E077AD', '#ffffff');
INSERT INTO cards (category, name_ru, name_en, desc_ru, desc_en, quote_ru, quote_en, facts_ru, facts_en, answers_en, drawing_id, pixelated_id, screenshot_id, bg_color_1, answers_ru, completed, bg_color_2, text_color) VALUES ('movie', 'Крестный отец', 'The Godfather', e'Со Второй мировой войны возвращается любимый сын Дона Корлеоне, Майкл. Майкл, герой войны, гордость семьи, не выражает желания заняться жестоким семейным бизнесом. Дон Корлеоне ведёт дела по старым правилам, но наступают иные времена, и появляются люди, желающие изменить сложившиеся порядки. На Дона Корлеоне совершается покушение', 'Don Vito Corleone, head of a mafia family, decides to hand over his empire to his youngest son Michael. However, his decision unintentionally puts the lives of his loved ones in grave danger', e'Я сделаю ему предложение, от которого он не сможет отказаться', 'I''m going to make him an offer he can''t refuse', '["Фильм снят по мотивам романа Ма-\nрио Пьюзо «Крёстный отец»", "Марио Пьюзо был полностью разо-\nрен, когда подписывал контракт с \n«Random House» на издание «Крест-\nного отца»", "Прототипом Вито Корлеоне являлся \nзнаменитый гангстер Джозеф «Джо» \nБонанно"]', '["During an early shot of the scene where Vito Corleone returns home and his people carry him up the stairs, Marlon Brando put weights under his body on the bed as a prank, to make it harder to lift him", "The cat held by Marlon Brando in the opening scene was a stray that Coppola found while on the lot at \"Paramount Pictures,\" and was not originally called for in the script. So content was the cat that its purring muffled some of Brando''s dialogue and, as a result, most of his lines had to be looped", "Richard S. Castellano ad-libbed the line \"Leave the gun, take the cannoli"]', '["The Godfather", "Se7en", "Scarface", "Goodfellas", "The Irishman"]', 'BQACAgIAAxkBAAIBEWUVQ4nOPzSyqBFJcuH-vcgEZk9PAAKxNAACrXCwSMozMTfw9qJRMAQ', 'BQACAgIAAxkBAAIBE2UVQ5Fx4VZAlrlioNMQLZGDEwMPAAKyNAACrXCwSGzhhaJGoE2zMAQ', 'BQACAgIAAxkBAAIBFWUVQ5m1dtA1WlOtmNwV3qsMlE82AAKzNAACrXCwSLAseVJ6PVRtMAQ', '#F08E8E', '["Крестный отец", "Семь", "Лицо со шрамом", "Славные Парни", "Ирландец"]', true, '#667497', '#ffffff');
