INSERT INTO public.cards (id, category, name_ru, name_en, desc_ru, desc_en, quote_ru, quote_en, facts_ru, facts_en, answers_en, drawing_id, pixelated_id, screenshot_id, bg_color_1, answers_ru, completed, bg_color_2, text_color) VALUES (19, 'movie', 'Железный Человек', 'Iron Man', 'Миллиардер-изобретатель Тони Старк попадает в плен к Афганским террористам, которые пытаются заставить его создать оружие массового поражения. В тайне от своих захватчиков Старк конструирует высокотехнологичную киберброню, которая помогает ему сбежать. Однако по возвращении в США он узнаёт, что в совете директоров его фирмы плетётся заговор, чреватый страшными последствиями. Используя своё последнее изобретение, Старк пытается решить проблемы своей компании радикально...', 'After being held captive in an Afghan cave, billionaire engineer Tony Stark creates a unique weaponized suit of armor to fight evil.', 'Что здесь происходит? Скажем, это не худшее, за чем ты меня застукала.', 'What is going on here? Let''s face it, this is not the worst thing you''ve caught me doing.', '["Дизайн костюма «Марк 3» был создан Филом Сандерсом и Ади Грановом (художник комиксов про Железного человека). Сам костюм, затем, был сконструирован Stan Winston Studios.", "Это первый фильм, полностью финансировавшийся Marvel Studios.", "Джон Фавро решил снимать фильм в Калифорнии, так как, по его мнению, слишком много фильмов про супергероев было снято на Восточном побережье, в частности в Нью-Йорке."]', '["To avoid spoilers about the final press conference, the extras were told that it was a dream sequence.", "This is Marvel Studios'' first self-financed movie.", "Roughly 450 separate pieces make up the Iron Man suit."]', '["Iron Man", "The Dark Knight", "Transformers", "Spider-Man", "RoboCop"]', 'BQACAgIAAxkBAAPeZSCOCwQY00w1mmM5r5MXFIwUIncAAh8zAAJU9AlJQYPPO61x-LswBA', 'BQACAgIAAxkBAAPgZSCOMFVvz8zCVAABJ9WyXC7fLFIvAAIgMwACVPQJSaVbNPjPSn7rMAQ', 'BQACAgIAAxkBAAPiZSCOW46GjAuUVj1ZlIyp-6yIcxQAAiEzAAJU9AlJepk3WSr8eA4wBA', '#DC4F4B', '["Железный Человек", "Темный Рыцарь", "Трансформеры", "Человек-Паук", "Робокоп"]', true, '#FFE394', '#FFFFFF');
INSERT INTO public.cards (id, category, name_ru, name_en, desc_ru, desc_en, quote_ru, quote_en, facts_ru, facts_en, answers_en, drawing_id, pixelated_id, screenshot_id, bg_color_1, answers_ru, completed, bg_color_2, text_color) VALUES (14, 'movie', 'Начало', 'Inception', 'Кобб – талантливый вор, лучший из лучших в опасном искусстве извлечения: он крадет ценные секреты из глубин подсознания во время сна, когда человеческий разум наиболее уязвим. Редкие способности Кобба сделали его ценным игроком в привычном к предательству мире промышленного шпионажа, но они же превратили его в извечного беглеца и лишили всего, что он когда-либо любил.', 'A thief who steals corporate secrets through the use of dream-sharing technology is given the inverse task of planting an idea into the mind of a C.E.O., but his tragic past may doom the project and his team to disaster.', 'Мне нужен архитектор такой же классный, каким был я.', 'Dreams feel real while we''re in them. It''s only when we wake up that we realize something was actually strange.', '["Первоначальное название сценария — «Стрела Оливера».", "Леонардо ДиКаприо был первым актером, который дал согласие на участие в проекте.", "На одном из этапов производства студия предлагала сделать картину в 3D, однако Нолан отказался, возразив, что трехмерный формат будет лишь отвлекать от истории, рассказанной в фильме."]', '["In an effort to combat confusion, television broadcasts in Japan include text in the upper-left corner of the screen to remind viewers in which level of the dream a specific scene takes place.", "Joseph Gordon-Levitt performed all but one of his own stunts during the fight scene in the spinning hallway.", "Leonardo DiCaprio was the only choice for the role of Cobb for writer, producer, and director Christopher Nolan and producer Emma Thomas."]', '["Inception", "Fight Club", "The Prestige", "Interstellar", "Shutter Island"]', 'BQACAgIAAxkBAAMpZSB4KulwG_Jhuqix9YZQtvGDEpQAAq8yAAJU9AlJ39WOCsmEj3gwBA', 'BQACAgIAAxkBAAMrZSB4W9DuyT-nyKUfvhccEHlcCEsAArIyAAJU9AlJ5rYlFkbby1EwBA', 'BQACAgIAAxkBAAMtZSB4g7RTp82EOWDJ1Rjt5wbmh14AArUyAAJU9AlJZb3THvBzMrEwBA', '#886DAD', '["Начало", "Остров Проклятых", "Престиж", "Бойцовский Клуб", "Интерстеллар"]', true, '#FBC467', '#FFFFFF');
INSERT INTO public.cards (id, category, name_ru, name_en, desc_ru, desc_en, quote_ru, quote_en, facts_ru, facts_en, answers_en, drawing_id, pixelated_id, screenshot_id, bg_color_1, answers_ru, completed, bg_color_2, text_color) VALUES (15, 'movie', 'Побег из Шоушенка', 'The Shawshank Redemption', 'Бухгалтер Энди Дюфрейн обвинён в убийстве собственной жены и её любовника. Оказавшись в тюрьме под названием Шоушенк, он сталкивается с жестокостью и беззаконием, царящими по обе стороны решётки. Каждый, кто попадает в эти стены, становится их рабом до конца жизни. Но Энди, обладающий живым умом и доброй душой, находит подход как к заключённым, так и к охранникам, добиваясь их особого к себе расположения.', 'Over the course of several years, two convicts form a friendship, seeking consolation and, eventually, redemption through basic compassion.', 'Самое смешное, что на воле я был честным и прямым как стрела. Сел в тюрьму, чтобы стать преступником.', 'The funny thing is - on the outside, I was an honest man, straight as an arrow. I had to come to prison to be a crook.', '["Фильм снят по мотивам повести Стивена Кинга «Рита Хейуорт и спасение из Шоушенка»", "Съемки проходили в Мэнсфилдской исправительной колонии в штате Огайо. Тюрьма находилась в таком плачевном состоянии, что пришлось приводить её в должный вид.", "Оригинальная повесть Стивена Кинга была, по словам самого писателя, кульминацией всех его впечатлений от различных тюремных фильмов, которые он смотрел в детстве."]', '["Morgan Freeman''s favorite film of his own.", "Stephen King has considered this to be one of his favorite film adaptations based on his own work.", "Despite being widely considered as one of the greatest movies of all time, it didn''t receive a single Oscar win, though it was nominated for seven, including Best Picture."]', '["The Shawshank Redemption", "The Green Mile", "Escape from Alcatraz", "One Flew Over the Cuckoo''s Nest", "Papillon"]', 'BQACAgIAAxkBAANLZSB8YpbLQpgQ3uvz-qbJTOMS6LoAAtQyAAJU9AlJOgULon-fGrIwBA', 'BQACAgIAAxkBAANNZSB8gzqlDq2mq1axCKaSDBiJwxsAAtUyAAJU9AlJ_D1okAiCHbwwBA', 'BQACAgIAAxkBAANPZSB8p2-3RpDlHiKyiMo3GYbGwFQAAtcyAAJU9AlJWixDm73K4wYwBA', '#476494', '["Побег из Шоушенка", "Побег из Алькатраса", "Зеленая Миля", "Пролетая над Гнездом Кукушки", "Мотылек"]', true, '#E5B287', '#FFFFFF');
INSERT INTO public.cards (id, category, name_ru, name_en, desc_ru, desc_en, quote_ru, quote_en, facts_ru, facts_en, answers_en, drawing_id, pixelated_id, screenshot_id, bg_color_1, answers_ru, completed, bg_color_2, text_color) VALUES (16, 'movie', 'Зеленая Книга', 'Green Book', '1960-е годы. После закрытия нью-йоркского ночного клуба на ремонт вышибала Тони по прозвищу Болтун ищет подработку на пару месяцев. Как раз в это время Дон Ширли — утонченный светский лев, богатый и талантливый чернокожий музыкант, исполняющий классическую музыку — собирается в турне по южным штатам, где ещё сильны расистские убеждения и царит сегрегация. Он нанимает Тони в качестве водителя, телохранителя и человека, способного решать текущие проблемы. У этих двоих так мало общего, и эта поездка навсегда изменит жизнь обоих.', 'A working-class Italian-American bouncer becomes the driver for an African-American classical pianist on a tour of venues through the 1960s American South.', e'То есть я недостаточно черный, и недостаточно белый. Скажи, Тони, кто я такой?!', 'So if I''m not *black* enough and if I''m not *white* enough, and not *man* enough, then tell me, Tony, what am I?', '["Прототип героя Вигго Мортенсена — Тони Лип.", "Вместо Махершалы Али на фортепиано в фильме играл композитор Крис Бауэрс.", "Фильм посвящается ворону по прозвищу Ларри, обнаруженному неподалёку от съёмочной площадки. Птицу сбила машина, и заботу о ней взял на себя Вигго Мортенсен."]', '["Nick Vallelonga says he spent a lot of the shoot in tears because the actors'' portrayals of his parents were so vivid. \"Linda Cardellini, I can''t even look at all. She just makes me lose it.\"", "The film is dedicated to \"Larry the Crow,\" a bird that hung around the shooting location. Viggo Mortensen cared for the animal after it was hit by a car.", "The Virgin Mary necklace worn by Viggo Mortensen belonged to the real Tony Lip."]', '["Green Book", "The Green Mile", "Intouchables", "12 Years a Slave", "The Help"]', 'BQACAgIAAxkBAAN1ZSCCI76OXWLp0VFK9t6DpCywOHYAAvYyAAJU9AlJzAaG2LEghpcwBA', 'BQACAgIAAxkBAAN3ZSCCQemnRhb3OPZJwFKLkWpbnA8AAvgyAAJU9AlJ6OuZq7Js_dcwBA', 'BQACAgIAAxkBAAN5ZSCCYRIotsSWVw1env1SpNaaFMoAAvkyAAJU9AlJIgY6jF_mvJUwBA', '#6AC3C6', '["Зеленая Книга", "Зеленая Миля", "1+1", "Прислуга", "12 Лет Рабства"]', true, '#F9CC7D', '#4C6385');
INSERT INTO public.cards (id, category, name_ru, name_en, desc_ru, desc_en, quote_ru, quote_en, facts_ru, facts_en, answers_en, drawing_id, pixelated_id, screenshot_id, bg_color_1, answers_ru, completed, bg_color_2, text_color) VALUES (17, 'movie', 'День Сурка', 'Groundhog Day', 'Телевизионный комментатор Фил Коннорс каждый год приезжает в маленький городок в штате Пенсильвания на празднование Дня сурка. Но на этот раз веселье рискует зайти слишком далеко. Время сыграло с ним злую шутку: оно взяло да и остановилось.', 'A narcissistic, self-centered weatherman finds himself in a time loop on Groundhog Day, and the day keeps repeating until he gets it right.', 'Это идеальный день! Жаль, что все такими не бывают... Могут быть. Но для этого нужно поработать.', 'This day was perfect. You couldn''t have planned a day like this. Well, you can. It just takes an awful lot of work.', '["Во время съемок картины Билл Мюррей получил производственную травму. Он был дважды укушен сурком.", "Режиссер Харольд Рэмис планировал пригласить на исполнение главной роли Тома Хэнкса, но впоследствии признал, что Том «слишком хорош» для этой роли.", "Съемки картины велись не в Панксутони, а в городке Вудсток (штат Иллинойс). Между тем, Билл Мюррей и Харольд Рэмис посещали настоящую церемонию лично за год до выхода картины, в 1992 году."]', '["Bill Murray was bitten by the groundhog twice during shooting. Murray had to have anti rabies injections, because the bites were so severe.", "According to Director Harold Ramis, most of the time, when he tried to explain a scene to Bill Murray, Murray would interrupt and ask, \"Just tell me - good Phil or bad Phil?\"", "Harold Ramis originally wanted Tom Hanks for the lead role, but decided against it, saying that Hanks was \"too nice.\""]', '["Groundhog Day", "The Butterfly Effect", "Edge of Tomorrow", "Source Code", "Back to the Future"]', 'BQACAgIAAxkBAAOXZSCHCz5RpGCrTk2VBdxWN8mOJDUAAhUzAAJU9AlJyvuKJlejKTEwBA', 'BQACAgIAAxkBAAOZZSCHKuvYmvkjDXZ3s7rFzm6RhXkAAhYzAAJU9AlJL4XwhaXqFGcwBA', 'BQACAgIAAxkBAAObZSCHVPgUFq80P2BGF-sLMe48WxIAAhczAAJU9AlJQti-_nu3RxUwBA', '#9773B0', '["День Сурка", "Эффект Бабочки", "Грань Будущего", "Исходный код", "Назад в Будущее"]', true, '#9AD3D6', '#FFFFFF');
INSERT INTO public.cards (id, category, name_ru, name_en, desc_ru, desc_en, quote_ru, quote_en, facts_ru, facts_en, answers_en, drawing_id, pixelated_id, screenshot_id, bg_color_1, answers_ru, completed, bg_color_2, text_color) VALUES (18, 'movie', 'Интерстеллар', 'Interstellar', 'Когда засуха, пыльные бури и вымирание растений приводят человечество к продовольственному кризису, коллектив исследователей и учёных отправляется сквозь червоточину (которая предположительно соединяет области пространства-времени через большое расстояние) в путешествие, чтобы превзойти прежние ограничения для космических путешествий человека и найти планету с подходящими для человечества условиями.', 'When Earth becomes uninhabitable in the future, a farmer and ex-NASA pilot, Joseph Cooper, is tasked to pilot a spacecraft, along with a team of researchers, to find a new planet for humans.', 'Я не боюсь смерти. Я ведь старый физик. Я боюсь времени.', 'I''m not afraid of death. I''m an old physicist - I''m afraid of time.', '["Стивен Спилберг был прикреплен к проекту с 2006 года и нанял Джонатана Нолана писать сценарий, но отложил работу и занялся другими фильмами. В 2012, после окончательного ухода Спилберга, Джонатан Нолан предложил нанять своего брата Кристофера Нолана в качестве режиссера.", "Когда Спилберг ещё был прикреплен к проекту в качестве режиссёра, он собирался снимать в главной роли Уилла Смита. Позже, после ухода Спилберга с поста режиссёра, роль отошла Мэттью МакКонахи.", "Сценарий основан на научных работах физика-теоретика Кипа Торна."]', '["According to Dr. Kip Thorne, the largest degree of creative license in this movie are the clouds of the ice planet, which are structures that probably go beyond the material strength which ice would be able to support.", "According to Albert Einstein''s theory of General Relativity, it would take an infinite amount of time to cross the threshold of a black hole''s event horizon, as seen by a distant observer. The person crossing the threshold, however, would notice no change in the flow of time.", "Anne Hathaway suffered from hypothermia while filming in Iceland, due to the fact that her astronaut suit was open while filming scenes in the icy water."]', '["Interstellar", "Gravity", "The Martian", "Sunshine", "Arrival"]', 'BQACAgIAAxkBAAO8ZSCLAAExjAHDKwJ_w3nDG8B1vWNTAAIaMwACVPQJSQIwa8g_-PqnMAQ', 'BQACAgIAAxkBAAO-ZSCLMCkkriHwTsQUanb5zKS_b6sAAhszAAJU9AlJtIlHbLEHrn8wBA', 'BQACAgIAAxkBAAPAZSCLUlhSNygCg7DWfNP0aYs8ftMAAhwzAAJU9AlJfepIzodcbhEwBA', '#C4E4EA', '["Интерстеллар", "Гравитация", "Марсианин", "Пекло", "Прибытие"]', true, '#476494', '#FFFFFF');
INSERT INTO public.cards (id, category, name_ru, name_en, desc_ru, desc_en, quote_ru, quote_en, facts_ru, facts_en, answers_en, drawing_id, pixelated_id, screenshot_id, bg_color_1, answers_ru, completed, bg_color_2, text_color) VALUES (20, 'movie', 'Один Дома', 'Home Alone', 'Американское семейство отправляется из Чикаго в Европу, но в спешке сборов бестолковые родители забывают дома... одного из своих детей. Юное создание, однако, не теряется и демонстрирует чудеса изобретательности. И когда в дом залезают грабители, им приходится не раз пожалеть о встрече с милым крошкой.', 'An eight-year-old troublemaker, mistakenly left home alone, must defend his home against a pair of burglars on Christmas eve.', 'Журнал «Playboy». Все голые, аж смотреть тошно.', 'Playboy magazine. No clothes on anybody. Sickening.', '["Фильм, который Кевин просматривает на видеомагнитофоне, не существует в природе. Этот фрагмент был специально отснят для фильма. Он носит название «Ангелы с грязными душами»", "Джефри Уайзмен, сыгравший роль не в меру любопытного соседа Мича Мерфи, проходил кастинг на получение роли Кевина.", "Фильм попал в Книгу Рекордов Гиннеса, как комедия, собравшая больше других денег в прокате (около 533,000,000$)."]', '["Joe Pesci deliberately avoided Macaulay Culkin on-set, because he wanted Culkin to think he was mean.", "Catherine O''Hara revealed in 2014 that Macaulay Culkin still calls her \"Mom\".", "The line \"You guys give up, or are you thirsty for more?\" was improvised."]', '["Home Alone", "Problem Child", "Dennis the Menace", "Richie Rich", "Surviving Christmas"]', 'BQACAgIAAxkBAAIBAAFlIJJzaIeCuCyepKkXlwOc1g5OpAACJzMAAlT0CUkX_eE9NPNdWTAE', 'BQACAgIAAxkBAAIBAmUgkpch4qtF44sRBEUH6kLXoBPuAAIoMwACVPQJSRHK8vxQU_rMMAQ', 'BQACAgIAAxkBAAIBBGUgkrXAP5FWULgZEDg7b6lQ2mDGAAIpMwACVPQJSYR8iLuoupz1MAQ', '#EE7875', '["Один Дома", "Трудный Ребенок", "Деннис-Мучитель", "Богатенький Ричи", "Пережить Рождество"]', true, '#4BACC5', '#FFFFFF');
